        function initData() {
            sortedData = {};
            const dataSource = currentTopic === 'Christmas' ? christmasData : bfcmData;
            const countryData = dataSource[currentCountry] || {};
            
            // --- 新增逻辑：年份动态替换 ---
            const currentYear = new Date().getFullYear().toString(); // 获取当前年份 (例如 "2025")
            const oldYear = "2024"; // 数据源中的旧年份
            // ---------------------------

            for (const [key, val] of Object.entries(countryData)) {
                

                let processedList = val.map(item => {
                    if (typeof item === 'string') {

                        return item.replace(oldYear, currentYear);
                    } else {

                        return {
                            t: item.t.replace(oldYear, currentYear),
                            e: item.e.replace(oldYear, currentYear)
                        };
                    }
                });

                // 2. 再进行排序 
                if(['DE', 'IT', 'ES', 'FR', 'JP', 'MX'].includes(currentCountry)) {
                    sortedData[key] = processedList.sort((a, b) => a.t.localeCompare(b.t));
                } else {
                    sortedData[key] = processedList.sort((a, b) => a.localeCompare(b));
                }
            }

            // 重置当前分类
            if (!sortedData[currentCategory]) {
                const availableCats = Object.keys(sortedData);
                if(availableCats.length > 0) currentCategory = availableCats[0];
            }
        }

