      function getTerm(item) { if (['DE', 'IT', 'ES', 'FR', 'JP', 'MX'].includes(currentCountry)) { return item.t; }   return item;   }


        // --- DOM RENDERERS ---
        const sidebar = document.getElementById('sidebar');
        const mainContent = document.getElementById('mainContent');
        const searchInput = document.getElementById('searchInput');

        function renderSidebar() {
            sidebar.innerHTML = "";
            const cats = Object.keys(sortedData);
            
            if(cats.length === 0) { 
                sidebar.innerHTML = `<div style="padding:20px; color:#999; font-size:0.9rem; text-align:center;">No data available</div>`; 
                return; 
            }

            // --- 新增逻辑 1：计算总数 ---
            let totalKeywords = 0;
            cats.forEach(cat => {
                // 累加每个分类数组的长度
                totalKeywords += sortedData[cat].length;
            });

            // --- 新增逻辑 2：插入总数显示模块 ---
            const statsDiv = document.createElement('div');
            statsDiv.className = 'sidebar-stats';
            // 使用 var(--primary) 让数字颜色随圣诞/黑五主题变化
            statsDiv.innerHTML = `
                <div class="stats-label">Total Keywords</div>
                <div class="stats-number" style="color: var(--primary)">
                    ${totalKeywords}
                    <small>kws</small>
                </div>
            `;
            sidebar.appendChild(statsDiv);
            // ---------------------------

            // 原有逻辑：生成分类按钮
            cats.forEach(cat => {
                const count = sortedData[cat].length;
                const btn = document.createElement('div');
                btn.className = `nav-item ${cat === currentCategory ? 'active' : ''}`;
                const iconClass = categoryIcons[cat] || "fa-tag";
                
                btn.innerHTML = `
                    <div class="nav-content">
                        <i class="fa-solid ${iconClass}"></i>
                        <span>${cat}</span>
                    </div>
                    <span class="nav-count">${count}</span>
                `;
                
                btn.onclick = () => {
                    currentCategory = cat;
                    searchInput.value = "";
                    renderSidebar();
                    renderContent();
                };
                sidebar.appendChild(btn);
            });
        }

function renderContent(filter = "") {
            mainContent.innerHTML = "";
            const lowerFilter = filter.toLowerCase();
            let categoriesToShow = lowerFilter ? Object.keys(sortedData) : [currentCategory];
            let foundAny = false;

            categoriesToShow.forEach(cat => {
                const keywords = sortedData[cat];
                if(!keywords) return;
                
                // 1. 筛选数据
                const filteredList = keywords.filter(item => {
                    if (['DE', 'IT', 'ES', 'FR', 'JP', 'MX'].includes(currentCountry)) {
                        return item.t.toLowerCase().includes(lowerFilter) || item.e.toLowerCase().includes(lowerFilter);
                    } else {
                        return item.toLowerCase().includes(lowerFilter);
                    }
                });

                if (filteredList.length > 0) {
                    foundAny = true;
                    
                    const section = document.createElement('div');
                    section.className = "category-section";
                    const iconClass = categoryIcons[cat] || "fa-tag";
                    const themeColor = currentTopic === 'BFCM' ? 'var(--secondary)' : 'var(--primary)';
                    
                    // 2. 生成类目头部
                    section.innerHTML = `
                        <div class="category-header">
                            <div class="category-title">
                                <i class="fa-solid ${iconClass}" style="color:${themeColor};"></i>
                                ${cat}
                                <span class="cat-count-badge">(${filteredList.length})</span>
                            </div>
                            <button class="btn-copy-list" onclick="copyCategoryList('${cat}', '${lowerFilter.replace(/'/g, "\\'")}')">
                                <i class="fa-solid fa-layer-group"></i> Copy List
                            </button>
                        </div>
                    `;

                    const grid = document.createElement('div');
                    grid.className = "keywords-grid";

                    // --- [新增] A-Z 标题逻辑变量 ---
                    let lastLetter = ""; 

                    filteredList.forEach(item => {
                        const term = getTerm(item);
                        
                        // --- [新增] A-Z 标题逻辑 ---
                        if (filter === "") {
                            const currentLetter = term.charAt(0).normalize("NFD").replace(/[\u0300-\u036f]/g, "").toUpperCase();
                            if (currentLetter !== lastLetter) {
                                const header = document.createElement('div');
                                header.className = 'letter-header'; 
                                header.innerText = currentLetter;
                                grid.appendChild(header);
                                lastLetter = currentLetter;
                            }
                        }
                        // --------------------------

                        const isSelected = selectedKeywords.has(term);
                        const chip = document.createElement('div');
                        chip.className = `keyword-chip ${isSelected ? 'selected' : ''}`;
                        chip.dataset.word = term;
                        
                        let contentHTML = "";
                        if (['DE', 'IT', 'ES', 'FR', 'JP', 'MX'].includes(currentCountry)) {
                            contentHTML = `<div class="chip-text-group"><span class="chip-main">${item.t}</span><span class="chip-sub">${item.e}</span></div>`;
                        } else {
                            contentHTML = `<span class="chip-main">${item}</span>`;
                        }
                        
                        chip.innerHTML = `${contentHTML}<i class="fa-${isSelected ? 'solid fa-check-circle' : 'solid fa-plus'}"></i>`;
                        chip.onclick = () => toggleSelection(term);
                        grid.appendChild(chip);
                    });

                    section.appendChild(grid);
                    mainContent.appendChild(section);
                }
            });

            if (!foundAny) {
                mainContent.innerHTML = `
                    <div style="text-align:center; padding:50px; color:#999;">
                        <i class="fa-solid fa-magnifying-glass" style="font-size:3rem; opacity:0.3; margin-bottom:20px;"></i>
                        <h3>No matches found</h3>
                    </div>
                `;
            }
        }
        function toggleSelection(term) {
            if (selectedKeywords.has(term)) selectedKeywords.delete(term); else selectedKeywords.add(term);
            const chips = document.querySelectorAll(`.keyword-chip[data-word="${term}"]`);
            chips.forEach(chip => {
                const isSelected = selectedKeywords.has(term);
                chip.classList.toggle('selected', isSelected);
                chip.querySelector('i').className = isSelected ? "fa-solid fa-check-circle" : "fa-solid fa-plus";
            });
            updateSelectionPanel();
        }

        function updateSelectionPanel() {
            const listEl = document.getElementById('selectedList');
            const count = selectedKeywords.size;
            document.getElementById('selectionCount').textContent = `${count} items`;
            document.getElementById('mobileCount').textContent = `${count} selected`;
            listEl.innerHTML = "";
            if (count === 0) { listEl.innerHTML = '<li style="padding:30px 20px; text-align:center; color:#999; font-size:0.9rem;"><i class="fa-regular fa-square-check" style="font-size:2rem; margin-bottom:10px; opacity:0.5;"></i><br>Select keywords to build your list.</li>'; return; }
            selectedKeywords.forEach(term => {
                const li = document.createElement('li');
                li.className = 'selected-item';
                li.innerHTML = `<span style="font-weight:500">${term}</span><i class="fa-solid fa-xmark remove-btn" onclick="toggleSelection('${term}')"></i>`;
                listEl.appendChild(li);
            });
        }

        function clearSelection() {
            selectedKeywords.clear();
            document.querySelectorAll('.keyword-chip').forEach(c => { c.classList.remove('selected'); c.querySelector('i').className = "fa-solid fa-plus"; });
            updateSelectionPanel();
        }

        function copySelection() {
            if (selectedKeywords.size === 0) return showToast("No keywords selected!", true);
            const text = Array.from(selectedKeywords).join('\n');
            navigator.clipboard.writeText(text).then(() => showToast(`Copied ${selectedKeywords.size} items!`));
        }

        window.copyCategoryList = function(category, filterString) {
            const keywords = sortedData[category];
            const lowerFilter = filterString.toLowerCase();
        

            const filteredItems = keywords.filter(item => {
                 if (['DE', 'IT', 'ES', 'FR', 'JP', 'MX'].includes(currentCountry)) { 
                     return item.t.toLowerCase().includes(lowerFilter) || item.e.toLowerCase().includes(lowerFilter);
                 } else {
                     return item.toLowerCase().includes(lowerFilter);
                 }
            });
            const listToCopy = filteredItems.map(item => getTerm(item));
            if (listToCopy.length === 0) return;
            navigator.clipboard.writeText(listToCopy.join('\n')).then(() => showToast(`Copied ${listToCopy.length} items from ${category}!`));
        };

        // --- Modal Logic ---
        function openHelpModal() { document.getElementById('helpModal').classList.add('open'); }
        function openAboutModal() { document.getElementById('aboutModal').classList.add('open'); }
        function closeModal(e, modalId, force = false) { 
            if (force || e.target.classList.contains('modal-overlay')) {
                document.getElementById(modalId).classList.remove('open');
            }
        }

        // --- Utils ---
        let toastTimeout;
        function showToast(msg, isError = false) {
            const t = document.getElementById('toast');
            document.getElementById('toastMsg').textContent = msg;
            const icon = t.querySelector('i');
            icon.className = isError ? "fa-solid fa-circle-exclamation" : "fa-solid fa-check-circle";
            icon.style.color = isError ? "#f44336" : "#4caf50";
            t.classList.add('show');
            clearTimeout(toastTimeout);
            toastTimeout = setTimeout(() => t.classList.remove('show'), 2000);
        }
        function toggleMobilePanel() { document.getElementById('selectionPanel').classList.toggle('open'); }

        // --- INIT ---
        initData(); renderSidebar(); renderContent();
        searchInput.addEventListener('input', (e) => {
            if(e.target.value) document.querySelectorAll('.nav-item').forEach(el => el.classList.remove('active'));
            else renderSidebar();
            renderContent(e.target.value);
        });
